code.installcode:
ob_start();
include(CWD.'/cometchat/install.php');
$includedphp = ob_get_contents();
copy(CWD.'/cometchat_plugin/cpnav_cometchat_panel.xml',CWD.'/includes/xml/cpnav_cometchat_panel.xml');

$sql = "SELECT * FROM  `setting` WHERE varname LIKE  'push_key' AND product LIKE  'tapatalk'";
$result = $vbulletin->db->query($sql);
$row = mysqli_fetch_assoc($result);
if(!empty($row['value'])){
  $pushUrl = $row['value'];

  $file = CWD.'/cometchat/extensions/tapatalk/config.php';
  $content = @file_get_contents($file);

  if ($content != '') {
    $myvar = "define('PUSH_KEY' , '$pushUrl');";

    $replacement = "<?php \n\n//////////////////////\n\n/* SETTINGS START */\r\n\r\n".$myvar."\r\n\r\n/* SETTINGS END */\n\n////////////////////";

    $content = $replacement;
    $f = fopen($file,'w');
    if($f) {
      fwrite($f, $content);
      fclose($f);
    }
  }
}

ob_end_clean();

